package calproject;

public class Length {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i[] = {1,2,3};
		String myString = "Love wins.";
		
		// .length says the length of the array
		System.out.println("There are " +i.length + " positions in the array.");
		
		// .length() says the length of a string
		System.out.println("The length of myString is " + myString.length());
	}

}
